import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [woods, setWoods] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', stock: '', price: '', image: null });

  useEffect(() => {
    fetchWoods();
  }, []);

  const fetchWoods = async () => {
    const res = await axios.get('http://localhost:5000/api/woods');
    setWoods(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('description', form.description);
    formData.append('stock', form.stock);
    formData.append('price', form.price);
    if (form.image) formData.append('image', form.image);

    await axios.post('http://localhost:5000/api/woods', formData);
    fetchWoods();
    setForm({ name: '', description: '', stock: '', price: '', image: null });
  };

  return (
    <div className="App">
      <header>
        <h1>Suohou's Timber Mill</h1>
        <p>Premium Timber Inventory</p>
      </header>
      
      <section className="inventory">
        <h2>Woods in Stock</h2>
        <div className="woods-grid">
          {woods.map(wood => (
            <div key={wood._id} className="wood-card">
              <img src={`http://localhost:5000/uploads/${wood.image}`} alt={wood.name} />
              <h3>{wood.name}</h3>
              <p>{wood.description}</p>
              <p>Stock: {wood.stock} | Price: ${wood.price}</p>
            </div>
          ))}
        </div>
      </section>
      
      <section className="admin">
        <h2>Admin: Upload New Wood</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Name" value={form.name} onChange={e => setForm({...form, name: e.target.value})} required />
          <textarea placeholder="Description" value={form.description} onChange={e => setForm({...form, description: e.target.value})} required />
          <input type="number" placeholder="Stock" value={form.stock} onChange={e => setForm({...form, stock: e.target.value})} required />
          <input type="number" step="0.01" placeholder="Price" value={form.price} onChange={e => setForm({...form, price: e.target.value})} required />
          <input type="file" onChange={e => setForm({...form, image: e.target.files[0]})} required />
          <button type="submit">Upload</button>
        </form>
      </section>
    </div>
  );
}

export default App;
