import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ITEMS_PER_PAGE = 5;
const LOW_STOCK_LIMIT = 10;

function AdminPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });

  const [woods, setWoods] = useState([]);
  const [contacts, setContacts] = useState([]);

  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);

  /* ===============================
     ANALYTICS
  ================================ */
  const [analytics, setAnalytics] = useState({
    totalProducts: 0,
    totalStock: 0,
    lowStock: 0,
    enquiries: 0,
  });

  const [form, setForm] = useState({
    name: '',
    description: '',
    stock: '',
    price: '',
    image: null,
  });

  const [editId, setEditId] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    description: '',
    stock: '',
    price: '',
  });

  /* ===============================
     AUTH CHECK
  ================================ */
  useEffect(() => {
    const token = localStorage.getItem('admin_token');
    if (token) {
      setIsLoggedIn(true);
      fetchWoods();
      fetchContacts();
    }
  }, []);

  /* ===============================
     ANALYTICS UPDATE
  ================================ */
  useEffect(() => {
    const totalStock = woods.reduce(
      (sum, w) => sum + Number(w.stock || 0),
      0
    );

    const lowStock = woods.filter(
      (w) => Number(w.stock) <= LOW_STOCK_LIMIT
    ).length;

    setAnalytics({
      totalProducts: woods.length,
      totalStock,
      lowStock,
      enquiries: contacts.length,
    });
  }, [woods, contacts]);

  const authHeader = () => ({
    headers: { Authorization: localStorage.getItem('admin_token') },
  });

  /* ===============================
     FETCH
  ================================ */
  const fetchWoods = async () => {
    const res = await axios.get('http://localhost:5000/api/woods');
    setWoods(res.data);
  };

  const fetchContacts = async () => {
    const res = await axios.get(
      'http://localhost:5000/api/contact',
      authHeader()
    );
    setContacts(res.data);
  };

  /* ===============================
     LOGIN
  ================================ */
  const handleLogin = async (e) => {
    e.preventDefault();
    const res = await axios.post(
      'http://localhost:5000/api/admin/login',
      loginForm
    );
    localStorage.setItem('admin_token', res.data.token);
    setIsLoggedIn(true);
    fetchWoods();
    fetchContacts();
  };

  const handleLogout = () => {
    localStorage.removeItem('admin_token');
    setIsLoggedIn(false);
  };

  /* ===============================
     CRUD
  ================================ */
  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([k, v]) => v && formData.append(k, v));
    await axios.post(
      'http://localhost:5000/api/woods',
      formData,
      authHeader()
    );
    setForm({ name: '', description: '', stock: '', price: '', image: null });
    fetchWoods();
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this item?')) return;
    await axios.delete(
      `http://localhost:5000/api/woods/${id}`,
      authHeader()
    );
    fetchWoods();
  };

  const startEdit = (wood) => {
    setEditId(wood._id);
    setEditForm(wood);
  };

  const saveEdit = async (id) => {
    await axios.put(
      `http://localhost:5000/api/woods/${id}`,
      {
        ...editForm,
        stock: Number(editForm.stock),
        price: Number(editForm.price),
      },
      authHeader()
    );
    setEditId(null);
    fetchWoods();
  };

  const handleDeleteContact = async (id) => {
    if (!window.confirm('Delete enquiry?')) return;
    await axios.delete(
      `http://localhost:5000/api/contact/${id}`,
      authHeader()
    );
    fetchContacts();
  };

  /* ===============================
     FILTER + PAGINATION
  ================================ */
  const filteredWoods = woods.filter((w) =>
    w.name.toLowerCase().includes(search.toLowerCase())
  );

  const totalPages = Math.ceil(filteredWoods.length / ITEMS_PER_PAGE);
  const paginatedWoods = filteredWoods.slice(
    (page - 1) * ITEMS_PER_PAGE,
    page * ITEMS_PER_PAGE
  );

  /* ===============================
     LOGIN PAGE
  ================================ */
  if (!isLoggedIn) {
    return (
      <section className="admin auth">
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin}>
          <input
            placeholder="Username"
            onChange={(e) =>
              setLoginForm({ ...loginForm, username: e.target.value })
            }
          />
          <input
            type="password"
            placeholder="Password"
            onChange={(e) =>
              setLoginForm({ ...loginForm, password: e.target.value })
            }
          />
          <button className="btn">Login</button>
        </form>
      </section>
    );
  }

  /* ===============================
     ADMIN PANEL
  ================================ */
  return (
    <section className="admin">
      <div className="admin-header">
        <h2>Admin Dashboard</h2>
        <button className="btn btn-secondary" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* ANALYTICS */}
      <h3>Analytics</h3>
      <div className="analytics-grid">
        {Object.entries(analytics).map(([k, v]) => (
          <div key={k} className="analytics-card">
            <h4>{k.replace(/([A-Z])/g, ' $1')}</h4>
            <h2>{v}</h2>
          </div>
        ))}
      </div>

      {/* SEARCH */}
      <input
        placeholder="Search timber..."
        value={search}
        onChange={(e) => {
          setSearch(e.target.value);
          setPage(1);
        }}
        style={{ margin: '20px 0', padding: '8px', width: '100%' }}
      />

      {/* UPLOAD */}
      <h3>Upload Timber</h3>
      <form onSubmit={handleUpload}>
        <input placeholder="Name" onChange={(e) => setForm({ ...form, name: e.target.value })} />
        <textarea placeholder="Description" onChange={(e) => setForm({ ...form, description: e.target.value })} />
        <input placeholder="Stock" onChange={(e) => setForm({ ...form, stock: e.target.value })} />
        <input placeholder="Price" onChange={(e) => setForm({ ...form, price: e.target.value })} />
        <input type="file" onChange={(e) => setForm({ ...form, image: e.target.files[0] })} />
        <button className="btn">Upload</button>
      </form>

      {/* STOCK */}
      <h3>Stock</h3>
      <div className="woods-grid">
        {paginatedWoods.map((wood) => (
          <div
            key={wood._id}
            className="wood-card"
            style={{
              border:
                wood.stock <= LOW_STOCK_LIMIT
                  ? '2px solid red'
                  : '1px solid #ddd',
            }}
          >
            {editId === wood._id ? (
              <>
                <input value={editForm.name} onChange={(e) => setEditForm({ ...editForm, name: e.target.value })} />
                <textarea value={editForm.description} onChange={(e) => setEditForm({ ...editForm, description: e.target.value })} />
                <input value={editForm.stock} onChange={(e) => setEditForm({ ...editForm, stock: e.target.value })} />
                <input value={editForm.price} onChange={(e) => setEditForm({ ...editForm, price: e.target.value })} />
                <button onClick={() => saveEdit(wood._id)}>Save</button>
              </>
            ) : (
              <>
                <h4>{wood.name}</h4>
                {wood.stock <= LOW_STOCK_LIMIT && (
                  <span style={{ color: 'red' }}>⚠ Low Stock</span>
                )}
                <p>{wood.description}</p>
                <p>Stock: {wood.stock}</p>
                <p>₹{wood.price}</p>
                <button onClick={() => startEdit(wood)}>Edit</button>
                <button onClick={() => handleDelete(wood._id)}>Delete</button>
              </>
            )}
          </div>
        ))}
      </div>

      {/* PAGINATION */}
      <div style={{ marginTop: '15px' }}>
        <button disabled={page === 1} onClick={() => setPage(page - 1)}>
          Prev
        </button>
        <span style={{ margin: '0 10px' }}>
          Page {page} of {totalPages}
        </span>
        <button disabled={page === totalPages} onClick={() => setPage(page + 1)}>
          Next
        </button>
      </div>

      {/* ENQUIRIES */}
      <h3>Enquiries</h3>
      {contacts.map((c) => (
        <div key={c._id} className="contact-card">
          <p>{c.name} — {c.phone}</p>
          <p>{c.message}</p>
          <button onClick={() => handleDeleteContact(c._id)}>Delete</button>
        </div>
      ))}
    </section>
  );
}

export default AdminPage;
