import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });

  const [woods, setWoods] = useState([]);
  const [contacts, setContacts] = useState([]);

  // ✅ ANALYTICS STATE (PHASE 7)
  const [analytics, setAnalytics] = useState({
    totalProducts: 0,
    totalStock: 0,
    lowStockCount: 0,
    totalEnquiries: 0,
  });

  const [form, setForm] = useState({
    name: '',
    description: '',
    stock: '',
    price: '',
    image: null,
  });

  const [editId, setEditId] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    description: '',
    stock: '',
    price: '',
  });

  /* ===============================
     CHECK LOGIN ON LOAD
  ================================ */
  useEffect(() => {
    const token = localStorage.getItem('admin_token');
    if (token) {
      setIsLoggedIn(true);
      fetchWoods();
      fetchContacts();
      fetchAnalytics(); // ✅
    }
  }, []);

  /* ===============================
     AUTH HEADER
  ================================ */
  const authHeader = () => ({
    headers: {
      Authorization: localStorage.getItem('admin_token'),
    },
  });

  /* ===============================
     FETCH DATA
  ================================ */
  const fetchWoods = async () => {
    const res = await axios.get('http://localhost:5000/api/woods');
    setWoods(res.data);
  };

  const fetchContacts = async () => {
    const res = await axios.get(
      'http://localhost:5000/api/contact',
      authHeader()
    );
    setContacts(res.data);
  };

  // ✅ FETCH ANALYTICS
  const fetchAnalytics = async () => {
    try {
      const res = await axios.get(
        'http://localhost:5000/api/admin/analytics',
        authHeader()
      );
      setAnalytics(res.data);
    } catch (err) {
      console.error('Analytics error', err);
    }
  };

  /* ===============================
     LOGIN
  ================================ */
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        'http://localhost:5000/api/admin/login',
        loginForm
      );
      localStorage.setItem('admin_token', res.data.token);
      setIsLoggedIn(true);
      fetchWoods();
      fetchContacts();
      fetchAnalytics(); // ✅
    } catch {
      alert('Invalid credentials');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('admin_token');
    setIsLoggedIn(false);
  };

  /* ===============================
     UPLOAD STOCK
  ================================ */
  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(form).forEach(([key, value]) =>
      value && formData.append(key, value)
    );

    await axios.post(
      'http://localhost:5000/api/woods',
      formData,
      authHeader()
    );
    setForm({ name: '', description: '', stock: '', price: '', image: null });
    fetchWoods();
    fetchAnalytics(); // ✅
  };

  /* ===============================
     DELETE STOCK
  ================================ */
  const handleDelete = async (id) => {
    if (!window.confirm('Delete this item?')) return;
    await axios.delete(
      `http://localhost:5000/api/woods/${id}`,
      authHeader()
    );
    fetchWoods();
    fetchAnalytics(); // ✅
  };

  /* ===============================
     EDIT STOCK
  ================================ */
  const startEdit = (wood) => {
    setEditId(wood._id);
    setEditForm(wood);
  };

  const saveEdit = async (id) => {
    await axios.put(
      `http://localhost:5000/api/woods/${id}`,
      {
        ...editForm,
        stock: Number(editForm.stock),
        price: Number(editForm.price),
      },
      authHeader()
    );
    setEditId(null);
    fetchWoods();
    fetchAnalytics(); // ✅
  };

  /* ===============================
     DELETE ENQUIRY
  ================================ */
  const handleDeleteContact = async (id) => {
    if (!window.confirm('Delete enquiry?')) return;
    await axios.delete(
      `http://localhost:5000/api/contact/${id}`,
      authHeader()
    );
    fetchContacts();
    fetchAnalytics(); // ✅
  };

  /* ===============================
     LOGIN PAGE
  ================================ */
  if (!isLoggedIn) {
    return (
      <section className="admin auth">
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin} className="admin-login-form">
          <input
            placeholder="Username"
            value={loginForm.username}
            onChange={(e) =>
              setLoginForm({ ...loginForm, username: e.target.value })
            }
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={loginForm.password}
            onChange={(e) =>
              setLoginForm({ ...loginForm, password: e.target.value })
            }
            required
          />
          <button className="btn">Login</button>
        </form>
      </section>
    );
  }

  /* ===============================
     ADMIN PANEL
  ================================ */
  return (
    <section className="admin">
      <div className="admin-header">
        <h2>Admin Dashboard</h2>
        <button className="btn btn-secondary" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* ✅ ANALYTICS CARDS */}
      <div className="analytics-grid">
        <div className="analytics-card">
          <h4>Total Products</h4>
          <p>{analytics.totalProducts}</p>
        </div>
        <div className="analytics-card">
          <h4>Total Stock</h4>
          <p>{analytics.totalStock}</p>
        </div>
        <div className="analytics-card">
          <h4>Low Stock</h4>
          <p>{analytics.lowStockCount}</p>
        </div>
        <div className="analytics-card">
          <h4>Enquiries</h4>
          <p>{analytics.totalEnquiries}</p>
        </div>
      </div>

      <h3>Upload New Timber</h3>
      <form onSubmit={handleUpload} className="admin-upload-form">
        <input placeholder="Name" value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })} required />
        <textarea placeholder="Description" value={form.description}
          onChange={(e) => setForm({ ...form, description: e.target.value })} required />
        <input placeholder="Stock" value={form.stock}
          onChange={(e) => setForm({ ...form, stock: e.target.value })} required />
        <input placeholder="Price" value={form.price}
          onChange={(e) => setForm({ ...form, price: e.target.value })} required />
        <input type="file"
          onChange={(e) => setForm({ ...form, image: e.target.files[0] })} required />
        <button className="btn">Upload</button>
      </form>

      <h3>Customer Enquiries</h3>
      <div className="contact-list">
        {contacts.map((c) => (
          <div key={c._id} className="contact-card">
            <p><strong>{c.name}</strong></p>
            <p>{c.phone}</p>
            <p>{c.message}</p>
            <button className="btn btn-secondary"
              onClick={() => handleDeleteContact(c._id)}>
              Delete
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default AdminPage;
