import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [woods, setWoods] = useState([]);
  const [form, setForm] = useState({ name: '', description: '', stock: '', price: '', image: null });
  const [contactForm, setContactForm] = useState({ name: '', email: '', phone: '', message: '' });
  const [contactStatus, setContactStatus] = useState('');

  useEffect(() => {
    fetchWoods();
  }, []);

  const fetchWoods = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/woods');
      setWoods(res.data);
    } catch (err) {
      console.error('Error fetching woods:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('description', form.description);
    formData.append('stock', form.stock);
    formData.append('price', form.price);
    if (form.image) formData.append('image', form.image);

    try {
      await axios.post('http://localhost:5000/api/woods', formData);
      fetchWoods();
      setForm({ name: '', description: '', stock: '', price: '', image: null });
    } catch (err) {
      console.error('Error uploading wood:', err);
    }
  };

  const handleContactSubmit = async (e) => {
    e.preventDefault();
    setContactStatus('');
    try {
      const res = await axios.post('http://localhost:5000/api/contact', contactForm);
      setContactStatus(res.data.message || 'Message sent!');
      setContactForm({ name: '', email: '', phone: '', message: '' });
    } catch (err) {
      console.error('Error sending contact message:', err);
      setContactStatus('Something went wrong. Please try again later.');
    }
  };

  return (
    <div className="App">
      <header>
        <h1>Suohou's Timber Mill</h1>
        <p>Premium Timber Inventory for Your Construction Needs</p>
      </header>

      {/* Customer view: inventory */}
      <section className="inventory">
        <h2>Available Timber Stock</h2>
        {woods.length === 0 ? (
          <p>No stock available right now. Please check back later.</p>
        ) : (
          <div className="woods-grid">
            {woods.map(wood => (
              <div key={wood._id} className="wood-card">
                {wood.image && (
                  <img
                    src={`http://localhost:5000/uploads/${wood.image}`}
                    alt={wood.name}
                  />
                )}
                <h3>{wood.name}</h3>
                <p>{wood.description}</p>
                <p><strong>Stock:</strong> {wood.stock}</p>
                <p><strong>Price:</strong> â‚¹{wood.price}</p>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Contact section for customers */}
      <section className="contact">
        <h2>Contact the Store</h2>
        <p className="contact-info">
          Visit us at: <strong>Suohou's Timber Mill, [Your Address Here]</strong><br />
          Call: <a href="tel:+911234567890">+91 12345 67890</a><br />
          WhatsApp: <a href="https://wa.me/911234567890" target="_blank" rel="noreferrer">Chat on WhatsApp</a>
        </p>

        <form className="contact-form" onSubmit={handleContactSubmit}>
          <input
            type="text"
            placeholder="Your Name"
            value={contactForm.name}
            onChange={e => setContactForm({ ...contactForm, name: e.target.value })}
            required
          />
          <input
            type="email"
            placeholder="Your Email"
            value={contactForm.email}
            onChange={e => setContactForm({ ...contactForm, email: e.target.value })}
          />
          <input
            type="tel"
            placeholder="Your Phone"
            value={contactForm.phone}
            onChange={e => setContactForm({ ...contactForm, phone: e.target.value })}
          />
          <textarea
            placeholder="Your message / requirements"
            value={contactForm.message}
            onChange={e => setContactForm({ ...contactForm, message: e.target.value })}
            required
          />
          <button type="submit">Send Message</button>
        </form>

        {contactStatus && <p className="contact-status">{contactStatus}</p>}
      </section>

      {/* Admin area (you will use this) */}
      <section className="admin">
        <h2>Admin: Upload New Timber Stock</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Name"
            value={form.name}
            onChange={e => setForm({ ...form, name: e.target.value })}
            required
          />
          <textarea
            placeholder="Description"
            value={form.description}
            onChange={e => setForm({ ...form, description: e.target.value })}
            required
          />
          <input
            type="number"
            placeholder="Stock"
            value={form.stock}
            onChange={e => setForm({ ...form, stock: e.target.value })}
            required
          />
          <input
            type="number"
            step="0.01"
            placeholder="Price"
            value={form.price}
            onChange={e => setForm({ ...form, price: e.target.value })}
            required
          />
          <input
            type="file"
            onChange={e => setForm({ ...form, image: e.target.files[0] })}
            required
          />
          <button type="submit">Upload</button>
        </form>
      </section>
    </div>
  );
}

export default App;
