import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AdminPage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });

  const [woods, setWoods] = useState([]);
  const [form, setForm] = useState({
    name: '',
    description: '',
    stock: '',
    price: '',
    image: null,
  });

  const [editId, setEditId] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    description: '',
    stock: '',
    price: '',
  });

  useEffect(() => {
    const saved = localStorage.getItem('admin_logged_in');
    if (saved === 'true') {
      setIsLoggedIn(true);
      fetchWoods();
    }
  }, []);

  const fetchWoods = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/woods');
      setWoods(res.data);
    } catch (err) {
      console.error('Error fetching woods:', err);
    }
  };

  // ---------------- LOGIN ----------------
  const handleLogin = (e) => {
    e.preventDefault();
    const { username, password } = loginForm;

    if (username === 'admin' && password === 'timber123') {
      setIsLoggedIn(true);
      localStorage.setItem('admin_logged_in', 'true');
      fetchWoods();
    } else {
      alert('Invalid credentials');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('admin_logged_in');
  };

  // ---------------- UPLOAD ----------------
  const handleUpload = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('description', form.description);
    formData.append('stock', form.stock);
    formData.append('price', form.price);
    if (form.image) formData.append('image', form.image);

    try {
      await axios.post('http://localhost:5000/api/woods', formData);
      setForm({ name: '', description: '', stock: '', price: '', image: null });
      fetchWoods();
    } catch (err) {
      console.error(err);
      alert('Error uploading item');
    }
  };

  // ---------------- DELETE ----------------
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this item?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/woods/${id}`);
      fetchWoods();
    } catch (err) {
      console.error(err);
      alert('Error deleting item');
    }
  };

  // ---------------- EDIT ----------------
  const startEdit = (wood) => {
    setEditId(wood._id);
    setEditForm({
      name: wood.name,
      description: wood.description,
      stock: wood.stock,
      price: wood.price,
    });
  };

  const cancelEdit = () => {
    setEditId(null);
  };

  const saveEdit = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/woods/${id}`, editForm);
      setEditId(null);
      fetchWoods();
    } catch (err) {
      console.error(err);
      alert('Error updating item');
    }
  };

  // ---------------- LOGIN PAGE ----------------
  if (!isLoggedIn) {
    return (
      <section className="admin auth">
        <h2>Admin Login</h2>
        <form onSubmit={handleLogin} className="admin-login-form">
          <input
            type="text"
            placeholder="Username"
            value={loginForm.username}
            onChange={(e) =>
              setLoginForm({ ...loginForm, username: e.target.value })
            }
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={loginForm.password}
            onChange={(e) =>
              setLoginForm({ ...loginForm, password: e.target.value })
            }
            required
          />
          <button type="submit" className="btn">
            Login
          </button>
        </form>
        <p className="note">
          Demo credentials: <strong>admin / timber123</strong>
        </p>
      </section>
    );
  }

  // ---------------- ADMIN PANEL ----------------
  return (
    <section className="admin">
      <div className="admin-header">
        <h2>Admin Panel</h2>
        <button onClick={handleLogout} className="btn btn-secondary">
          Logout
        </button>
      </div>

      <h3>Upload New Timber Stock</h3>
      <form onSubmit={handleUpload} className="admin-upload-form">
        <input
          type="text"
          placeholder="Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          required
        />
        <textarea
          placeholder="Description"
          value={form.description}
          onChange={(e) =>
            setForm({ ...form, description: e.target.value })
          }
          required
        />
        <input
          type="number"
          placeholder="Stock"
          value={form.stock}
          onChange={(e) => setForm({ ...form, stock: e.target.value })}
          required
        />
        <input
          type="number"
          step="0.01"
          placeholder="Price"
          value={form.price}
          onChange={(e) => setForm({ ...form, price: e.target.value })}
          required
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) =>
            setForm({ ...form, image: e.target.files[0] })
          }
          required
        />
        <button type="submit" className="btn">
          Upload
        </button>
      </form>

      <h3>Current Stock</h3>
      <div className="woods-grid">
        {woods.map((wood) => (
          <div key={wood._id} className="wood-card">
            {wood.image && (
              <img
                src={`http://localhost:5000/uploads/${wood.image}`}
                alt={wood.name}
              />
            )}

            {editId === wood._id ? (
              <>
                <input
                  type="text"
                  value={editForm.name}
                  onChange={(e) =>
                    setEditForm({ ...editForm, name: e.target.value })
                  }
                />
                <textarea
                  value={editForm.description}
                  onChange={(e) =>
                    setEditForm({
                      ...editForm,
                      description: e.target.value,
                    })
                  }
                />
                <input
                  type="number"
                  value={editForm.stock}
                  onChange={(e) =>
                    setEditForm({ ...editForm, stock: e.target.value })
                  }
                />
                <input
                  type="number"
                  value={editForm.price}
                  onChange={(e) =>
                    setEditForm({ ...editForm, price: e.target.value })
                  }
                />

                <button className="btn" onClick={() => saveEdit(wood._id)}>
                  Save
                </button>
                <button
                  className="btn btn-secondary"
                  onClick={cancelEdit}
                >
                  Cancel
                </button>
              </>
            ) : (
              <>
                <h4>{wood.name}</h4>
                <p>{wood.description}</p>
                <p>
                  <strong>Stock:</strong> {wood.stock}
                </p>
                <p>
                  <strong>Price:</strong> â‚¹{wood.price}
                </p>

                <button
                  className="btn"
                  onClick={() => startEdit(wood)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-secondary"
                  onClick={() => handleDelete(wood._id)}
                >
                  Delete
                </button>
              </>
            )}
          </div>
        ))}
      </div>
    </section>
  );
}

export default AdminPage;
